export class ClientsListComponent {
  constructor(apiService, authService, dialog, snackBar, router) {
    this.apiService = apiService;
    this.authService = authService;
    this.dialog = dialog;
    this.snackBar = snackBar;
    this.router = router;
    this.displayedColumns = ['nome', 'documento', 'email', 'status', 'totalContratos', 'valorTotal', 'actions'];
    this.dataSource = new MatTableDataSource();
    this.loading = false;
    this.searchTerm = '';
    this.statusFilter = '';
    // Pagination
    this.totalItems = 0;
    this.pageSize = 10;
    this.pageSizeOptions = [5, 10, 25, 50];
    // Status options
    this.statusOptions = [{
      value: '',
      label: 'Todos'
    }, {
      value: 'ativo',
      label: 'Ativo'
    }, {
      value: 'inativo',
      label: 'Inativo'
    }, {
      value: 'suspenso',
      label: 'Suspenso'
    }];
  }
  ngOnInit() {
    this.loadClients();
  }
  ngAfterViewInit() {
    this.dataSource.paginator = this.paginator;
    this.dataSource.sort = this.sort;
  }
  loadClients() {
    this.loading = true;
    const filters = {
      search: this.searchTerm,
      status: this.statusFilter,
      sortBy: this.sort?.active || 'nome',
      sortOrder: this.sort?.direction || 'asc'
    };
    const page = this.paginator?.pageIndex + 1 || 1;
    const limit = this.paginator?.pageSize || this.pageSize;
    this.apiService.getClients(page, limit, filters).subscribe({
      next: response => {
        this.dataSource.data = response.items;
        this.totalItems = response.total;
        this.loading = false;
      },
      error: error => {
        this.snackBar.open(`Erro ao carregar clientes: ${error.message}`, 'Fechar', {
          duration: 5000
        });
        this.loading = false;
      }
    });
  }
  applyFilter() {
    this.paginator.firstPage();
    this.loadClients();
  }
  clearFilters() {
    this.searchTerm = '';
    this.statusFilter = '';
    this.applyFilter();
  }
  onPageChange() {
    this.loadClients();
  }
  onSortChange() {
    this.paginator.firstPage();
    this.loadClients();
  }
  // Actions
  canCreate() {
    return this.authService.hasPermission('clients:create');
  }
  canEdit() {
    return this.authService.hasPermission('clients:update');
  }
  canDelete() {
    return this.authService.hasPermission('clients:delete');
  }
  canView() {
    return this.authService.hasPermission('clients:read');
  }
  createClient() {
    this.router.navigate(['/clients/new']);
  }
  editClient(client) {
    this.router.navigate(['/clients', client.id, 'edit']);
  }
  viewClient(client) {
    this.router.navigate(['/clients', client.id]);
  }
  deleteClient(client) {
    const dialogRef = this.dialog.open(ConfirmDialogComponent, {
      width: '400px',
      data: {
        title: 'Confirmar Exclusão',
        message: `Tem certeza que deseja excluir o cliente "${client.nome}"?`,
        confirmText: 'Excluir',
        cancelText: 'Cancelar'
      }
    });
    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        // Simulating API call - in a real app, this would call the API
        const index = this.dataSource.data.findIndex(c => c.id === client.id);
        if (index > -1) {
          const newData = [...this.dataSource.data];
          newData.splice(index, 1);
          this.dataSource.data = newData;
          this.snackBar.open('Cliente excluído com sucesso!', 'Fechar', {
            duration: 3000
          });
        }
      }
    });
  }
  toggleClientStatus(client) {
    const newStatus = client.status === 'ativo' ? 'inativo' : 'ativo';
    // Simulating API call - in a real app, this would call the API
    client.status = newStatus;
    this.snackBar.open(`Status do cliente alterado para ${newStatus}!`, 'Fechar', {
      duration: 3000
    });
  }
  viewContracts(client) {
    this.router.navigate(['/contracts'], {
      queryParams: {
        clientId: client.id
      }
    });
  }
  // Utility methods
  getStatusColor(status) {
    switch (status) {
      case 'ativo':
        return 'primary';
      case 'inativo':
        return 'accent';
      case 'suspenso':
        return 'warn';
      default:
        return '';
    }
  }
  getStatusIcon(status) {
    switch (status) {
      case 'ativo':
        return 'check_circle';
      case 'inativo':
        return 'radio_button_unchecked';
      case 'suspenso':
        return 'block';
      default:
        return 'help';
    }
  }
  exportClients() {
    this.loading = true;
    this.apiService.exportClients().subscribe({
      next: blob => {
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
