import { Component, OnInit, ViewChild, Inject } from '@angular/core';
import { MatTableDataSource } from '@angular/material/table';
import { MatPaginator } from '@angular/material/paginator';
import { MatSort } from '@angular/material/sort';
import { MatDialog, MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router } from '@angular/router';
import { ClientsApiService } from '../../shared/services/api.service';
import { AuthService } from '../../shared/guards/rbac.guard';

export interface Client {
  id: string;
  nome: string;
  documento: string;
  email: string;
  telefone: string;
  endereco: string;
  status: 'ativo' | 'inativo' | 'suspenso';
  totalContratos: number;
  valorTotal: number;
  createdAt: Date;
  updatedAt: Date;
}

@Component({
  selector: 'app-clients-list',
  templateUrl: './clients-list.component.html',
  styleUrls: ['./clients-list.component.scss'],
  standalone: false
})
export class ClientsListComponent implements OnInit {
  @ViewChild(MatPaginator) paginator!: MatPaginator;
  @ViewChild(MatSort) sort!: MatSort;

  displayedColumns: string[] = [
    'nome', 
    'documento', 
    'email', 
    'status', 
    'totalContratos', 
    'valorTotal', 
    'actions'
  ];

  dataSource = new MatTableDataSource<Client>();
  loading = false;
  searchTerm = '';
  statusFilter = '';

  // Pagination
  totalItems = 0;
  pageSize = 10;
  pageSizeOptions = [5, 10, 25, 50];

  // Status options
  statusOptions = [
    { value: '', label: 'Todos' },
    { value: 'ativo', label: 'Ativo' },
    { value: 'inativo', label: 'Inativo' },
    { value: 'suspenso', label: 'Suspenso' }
  ];

  constructor(
    private apiService: ClientsApiService,
    private authService: AuthService,
    private dialog: MatDialog,
    private snackBar: MatSnackBar,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadClients();
  }

  ngAfterViewInit(): void {
    this.dataSource.paginator = this.paginator;
    this.dataSource.sort = this.sort;
  }

  loadClients(): void {
    this.loading = true;

    const filters = {
      search: this.searchTerm,
      status: this.statusFilter,
      sortBy: this.sort?.active || 'nome',
      sortOrder: this.sort?.direction || 'asc'
    };

    const page = this.paginator?.pageIndex + 1 || 1;
    const limit = this.paginator?.pageSize || this.pageSize;

    this.apiService.getClients(page, limit, filters).subscribe({
      next: (response) => {
        this.dataSource.data = response.items;
        this.totalItems = response.total;
        this.loading = false;
      },
      error: (error) => {
        this.snackBar.open(`Erro ao carregar clientes: ${error.message}`, 'Fechar', { duration: 5000 });
        this.loading = false;
      }
    });
  }

  applyFilter(): void {
    this.paginator.firstPage();
    this.loadClients();
  }

  clearFilters(): void {
    this.searchTerm = '';
    this.statusFilter = '';
    this.applyFilter();
  }

  onPageChange(): void {
    this.loadClients();
  }

  onSortChange(): void {
    this.paginator.firstPage();
    this.loadClients();
  }

  // Actions
  canCreate(): boolean {
    return this.authService.hasPermission('clients:create');
  }

  canEdit(): boolean {
    return this.authService.hasPermission('clients:update');
  }

  canDelete(): boolean {
    return this.authService.hasPermission('clients:delete');
  }

  canView(): boolean {
    return this.authService.hasPermission('clients:read');
  }

  createClient(): void {
    this.router.navigate(['/clients/new']);
  }

  editClient(client: Client): void {
    this.router.navigate(['/clients', client.id, 'edit']);
  }

  viewClient(client: Client): void {
    this.router.navigate(['/clients', client.id]);
  }

  deleteClient(client: Client): void {
    const dialogRef = this.dialog.open(ConfirmDialogComponent, {
      width: '400px',
      data: {
        title: 'Confirmar Exclusão',
        message: `Tem certeza que deseja excluir o cliente "${client.nome}"?`,
        confirmText: 'Excluir',
        cancelText: 'Cancelar'
      }
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        // Simulating API call - in a real app, this would call the API
        const index = this.dataSource.data.findIndex(c => c.id === client.id);
        if (index > -1) {
          const newData = [...this.dataSource.data];
          newData.splice(index, 1);
          this.dataSource.data = newData;
          this.snackBar.open('Cliente excluído com sucesso!', 'Fechar', { duration: 3000 });
        }
      }
    });
  }

  toggleClientStatus(client: Client): void {
    const newStatus = client.status === 'ativo' ? 'inativo' : 'ativo';

    // Simulating API call - in a real app, this would call the API
    client.status = newStatus;
    this.snackBar.open(`Status do cliente alterado para ${newStatus}!`, 'Fechar', { duration: 3000 });
  }

  viewContracts(client: Client): void {
    this.router.navigate(['/contracts'], { queryParams: { clientId: client.id } });
  }

  // Utility methods
  getStatusColor(status: string): string {
    switch (status) {
      case 'ativo': return 'primary';
      case 'inativo': return 'accent';
      case 'suspenso': return 'warn';
      default: return '';
    }
  }

  getStatusIcon(status: string): string {
    switch (status) {
      case 'ativo': return 'check_circle';
      case 'inativo': return 'radio_button_unchecked';
      case 'suspenso': return 'block';
      default: return 'help';
    }
  }

  exportClients(): void {
    this.loading = true;

    this.apiService.exportClients().subscribe({
      next: (blob) => {
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `clientes_${new Date().toISOString().split('T')[0]}.xlsx`;
        link.click();
        window.URL.revokeObjectURL(url);

        this.loading = false;
        this.snackBar.open('Relatório exportado com sucesso!', 'Fechar', { duration: 3000 });
      },
      error: (error) => {
        // Fallback to CSV export if API fails
        const exportData = this.dataSource.filteredData.map(client => ({
          Nome: client.nome,
          Documento: client.documento,
          Email: client.email,
          Telefone: client.telefone,
          Status: client.status,
          'Total Contratos': client.totalContratos,
          'Valor Total': client.valorTotal
        }));

        const csvContent = this.convertToCSV(exportData);
        const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `clientes_${new Date().toISOString().split('T')[0]}.csv`;
        link.click();
        window.URL.revokeObjectURL(url);

        this.loading = false;
        this.snackBar.open('Relatório exportado como CSV (fallback)', 'Fechar', { duration: 3000 });
      }
    });
  }

  private convertToCSV(data: any[]): string {
    if (!data || data.length === 0) return '';

    const headers = Object.keys(data[0]);
    const csvRows = [
      headers.join(','),
      ...data.map(row => headers.map(header => `"${row[header] || ''}"`).join(','))
    ];

    return csvRows.join('\n');
  }
}

// Confirm Dialog Component (inline for simplicity)
@Component({
  selector: 'app-confirm-dialog',
  template: `
    <h2 mat-dialog-title>{{ data.title }}</h2>
    <mat-dialog-content>
      <p>{{ data.message }}</p>
    </mat-dialog-content>
    <mat-dialog-actions align="end">
      <button mat-button (click)="onCancel()">{{ data.cancelText }}</button>
      <button mat-raised-button color="warn" (click)="onConfirm()">{{ data.confirmText }}</button>
    </mat-dialog-actions>
  `,
  standalone: false
})
export class ConfirmDialogComponent {
  constructor(
    public dialogRef: MatDialogRef<ConfirmDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) {}

  onConfirm(): void {
    this.dialogRef.close(true);
  }

  onCancel(): void {
    this.dialogRef.close(false);
  }
}
<div class="clients-container">
  <!-- Header -->
  <div class="page-header">
    <div class="header-content">
      <h1>
        <mat-icon>people</mat-icon>
        Clientes
      </h1>
      <p class="page-description">Gerenciamento de clientes e relacionamentos comerciais</p>
    </div>

    <div class="header-actions">
      <button mat-raised-button color="primary" 
              (click)="createClient()" 
              *ngIf="canCreate()">
        <mat-icon>add</mat-icon>
        Novo Cliente
      </button>

      <button mat-stroked-button (click)="exportClients()" [disabled]="loading">
        <mat-icon>download</mat-icon>
        Exportar
      </button>
    </div>
  </div>

  <!-- Filters -->
  <mat-card class="filters-card">
    <mat-card-content>
      <div class="filters-row">
        <mat-form-field appearance="outline" class="search-field">
          <mat-label>Buscar clientes</mat-label>
          <input matInput [(ngModel)]="searchTerm" 
                 (keyup.enter)="applyFilter()"
                 placeholder="Nome, documento ou email">
          <mat-icon matSuffix>search</mat-icon>
        </mat-form-field>

        <mat-form-field appearance="outline">
          <mat-label>Status</mat-label>
          <mat-select [(ngModel)]="statusFilter" (selectionChange)="applyFilter()">
            <mat-option *ngFor="let status of statusOptions" [value]="status.value">
              {{ status.label }}
            </mat-option>
          </mat-select>
        </mat-form-field>

        <div class="filter-actions">
          <button mat-raised-button color="accent" (click)="applyFilter()" [disabled]="loading">
            <mat-icon>search</mat-icon>
            Filtrar
          </button>

          <button mat-button (click)="clearFilters()">
            <mat-icon>clear</mat-icon>
            Limpar
          </button>
        </div>
      </div>
    </mat-card-content>
  </mat-card>

  <!-- Data Table -->
  <mat-card class="table-card">
    <mat-card-content>
      <div class="table-container">
        <table mat-table [dataSource]="dataSource" 
               matSort (matSortChange)="onSortChange()" 
               class="clients-table">

          <!-- Nome Column -->
          <ng-container matColumnDef="nome">
            <th mat-header-cell *matHeaderCellDef mat-sort-header>Nome</th>
            <td mat-cell *matCellDef="let client">
              <div class="client-info">
                <div class="client-name">{{ client.nome }}</div>
                <div class="client-document">{{ client.documento }}</div>
              </div>
            </td>
          </ng-container>

          <!-- Documento Column -->
          <ng-container matColumnDef="documento">
            <th mat-header-cell *matHeaderCellDef mat-sort-header>Documento</th>
            <td mat-cell *matCellDef="let client">{{ client.documento }}</td>
          </ng-container>

          <!-- Email Column -->
          <ng-container matColumnDef="email">
            <th mat-header-cell *matHeaderCellDef>Email</th>
            <td mat-cell *matCellDef="let client">
              <a [href]="'mailto:' + client.email" class="email-link">{{ client.email }}</a>
            </td>
          </ng-container>

          <!-- Status Column -->
          <ng-container matColumnDef="status">
            <th mat-header-cell *matHeaderCellDef mat-sort-header>Status</th>
            <td mat-cell *matCellDef="let client">
              <mat-chip [color]="getStatusColor(client.status)" selected>
                <mat-icon>{{ getStatusIcon(client.status) }}</mat-icon>
                {{ client.status | titlecase }}
              </mat-chip>
            </td>
          </ng-container>

          <!-- Total Contratos Column -->
          <ng-container matColumnDef="totalContratos">
            <th mat-header-cell *matHeaderCellDef mat-sort-header>Contratos</th>
            <td mat-cell *matCellDef="let client">
              <div class="contracts-info">
                <span class="contracts-count">{{ client.totalContratos }}</span>
                <button mat-icon-button 
                        (click)="viewContracts(client)" 
                        matTooltip="Ver contratos"
                        *ngIf="client.totalContratos > 0">
                  <mat-icon>visibility</mat-icon>
                </button>
              </div>
            </td>
          </ng-container>

          <!-- Valor Total Column -->
          <ng-container matColumnDef="valorTotal">
            <th mat-header-cell *matHeaderCellDef mat-sort-header>Valor Total</th>
            <td mat-cell *matCellDef="let client">
              <span class="valor-total">{{ client.valorTotal | currency:'BRL':'symbol':'1.2-2' }}</span>
            </td>
          </ng-container>

          <!-- Actions Column -->
          <ng-container matColumnDef="actions">
            <th mat-header-cell *matHeaderCellDef>Ações</th>
            <td mat-cell *matCellDef="let client">
              <div class="actions-container">
                <button mat-icon-button 
                        (click)="viewClient(client)" 
                        matTooltip="Visualizar"
                        *ngIf="canView()">
                  <mat-icon>visibility</mat-icon>
                </button>

                <button mat-icon-button 
                        (click)="editClient(client)" 
                        matTooltip="Editar"
                        *ngIf="canEdit()">
                  <mat-icon>edit</mat-icon>
                </button>

                <button mat-icon-button 
                        [color]="client.status === 'ativo' ? 'warn' : 'primary'"
                        (click)="toggleClientStatus(client)" 
                        [matTooltip]="client.status === 'ativo' ? 'Desativar' : 'Ativar'"
                        *ngIf="canEdit()">
                  <mat-icon>{{ client.status === 'ativo' ? 'block' : 'check_circle' }}</mat-icon>
                </button>

                <button mat-icon-button 
                        color="warn"
                        (click)="deleteClient(client)" 
                        matTooltip="Excluir"
                        *ngIf="canDelete()">
                  <mat-icon>delete</mat-icon>
                </button>
              </div>
            </td>
          </ng-container>

          <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
          <tr mat-row *matRowDef="let row; columns: displayedColumns;" 
              class="table-row"
              (click)="viewClient(row)"></tr>
        </table>

        <!-- Loading State -->
        <div *ngIf="loading" class="loading-container">
          <app-loading message="Carregando clientes..."></app-loading>
        </div>

        <!-- Empty State -->
        <div *ngIf="!loading && dataSource.data.length === 0" class="empty-state">
          <mat-icon>people_outline</mat-icon>
          <h3>Nenhum cliente encontrado</h3>
          <p>Não há clientes que correspondam aos filtros aplicados.</p>
          <button mat-raised-button color="primary" 
                  (click)="createClient()" 
                  *ngIf="canCreate()">
            <mat-icon>add</mat-icon>
            Criar Primeiro Cliente
          </button>
        </div>
      </div>

      <!-- Pagination -->
      <mat-paginator [length]="totalItems"
                     [pageSize]="pageSize"
                     [pageSizeOptions]="pageSizeOptions"
                     (page)="onPageChange()"
                     showFirstLastButtons>
      </mat-paginator>
    </mat-card-content>
  </mat-card>

  <!-- Summary Cards -->
  <div class="summary-section" *ngIf="!loading && dataSource.data.length > 0">
    <div class="summary-cards">
      <mat-card class="summary-card">
        <mat-card-content>
          <div class="metric">
            <mat-icon>people</mat-icon>
            <div class="metric-content">
              <span class="metric-value">{{ totalItems }}</span>
              <span class="metric-label">Total de Clientes</span>
            </div>
          </div>
        </mat-card-content>
      </mat-card>

      <mat-card class="summary-card">
        <mat-card-content>
          <div class="metric">
            <mat-icon>check_circle</mat-icon>
            <div class="metric-content">
              <span class="metric-value">{{ getActiveClientsCount() }}</span>
              <span class="metric-label">Clientes Ativos</span>
            </div>
          </div>
        </mat-card-content>
      </mat-card>

      <mat-card class="summary-card">
        <mat-card-content>
          <div class="metric">
            <mat-icon>assignment</mat-icon>
            <div class="metric-content">
              <span class="metric-value">{{ getTotalContracts() }}</span>
              <span class="metric-label">Total de Contratos</span>
            </div>
          </div>
        </mat-card-content>
      </mat-card>

      <mat-card class="summary-card">
        <mat-card-content>
          <div class="metric">
            <mat-icon>monetization_on</mat-icon>
            <div class="metric-content">
              <span class="metric-value">{{ getTotalValue() | currency:'BRL':'symbol':'1.0-0' }}</span>
              <span class="metric-label">Valor Total</span>
            </div>
          </div>
        </mat-card-content>
      </mat-card>
    </div>
  </div>
</div>
