// Global Error Handler Service
import { Injectable, ErrorHandler } from '@angular/core';
import { Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class GlobalErrorHandler implements ErrorHandler {
  constructor(
    private router: Router
  ) {}

  handleError(error: any): void {
    console.error('Global error caught:', error);

    let errorMessage = 'Ocorreu um erro inesperado';
    let errorCode = 'UNKNOWN_ERROR';

    // Parse different error types
    if (error?.error?.message) {
      errorMessage = error.error.message;
      errorCode = error.error.code || 'API_ERROR';
    } else if (error?.message) {
      errorMessage = error.message;
      errorCode = error.name || 'JS_ERROR';
    }

    // Handle specific error types
    if (error.status === 401) {
      errorMessage = 'Sess√£o expirada. Fa√ßa login novamente.';
      errorCode = 'UNAUTHORIZED';
      this.router.navigate(['/login']);
    } else if (error.status === 403) {
      errorMessage = 'Voc√™ n√£o tem permiss√£o para esta a√ß√£o.';
      errorCode = 'FORBIDDEN';
    } else if (error.status === 404) {
      errorMessage = 'Recurso n√£o encontrado.';
      errorCode = 'NOT_FOUND';
    } else if (error.status >= 500) {
      errorMessage = 'Erro interno do servidor. Tente novamente mais tarde.';
      errorCode = 'SERVER_ERROR';
    }

    // Show user-friendly error message
    console.error(errorMessage);

    // Log to external service in production
    if (this.isProduction()) {
      this.logToExternalService({
        message: errorMessage,
        code: errorCode,
        details: error,
        timestamp: new Date(),
        userAgent: navigator.userAgent,
        url: window.location.href
      });
    }
  }

  private isProduction(): boolean {
    return !window.location.hostname.includes('localhost');
  }

  private logToExternalService(errorInfo: any): void {
    // Implement external logging service integration
    // Example: Sentry, LogRocket, etc.
    console.log('Logging to external service:', errorInfo);
  }
}

// HTTP Error Interceptor
import { Injectable } from '@angular/core';
import { HttpInterceptor, HttpRequest, HttpHandler, HttpErrorResponse } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError, retry } from 'rxjs/operators';

@Injectable()
export class ErrorInterceptor implements HttpInterceptor {
  constructor(private errorHandler: GlobalErrorHandler) {}

  intercept(req: HttpRequest<any>, next: HttpHandler): Observable<any> {
    return next.handle(req).pipe(
      retry(1), // Retry failed requests once
      catchError((error: HttpErrorResponse) => {
        // Transform HTTP errors to user-friendly format
        const transformedError = this.transformHttpError(error);

        // Let global error handler deal with it
        this.errorHandler.handleError(transformedError);

        return throwError(transformedError);
      })
    );
  }

  private transformHttpError(error: HttpErrorResponse): ErrorInfo {
    let message = 'Erro de comunica√ß√£o com o servidor';
    let code = `HTTP_${error.status}`;

    switch (error.status) {
      case 400:
        message = 'Dados inv√°lidos enviados';
        break;
      case 401:
        message = 'N√£o autorizado - fa√ßa login novamente';
        break;
      case 403:
        message = 'Acesso negado - permiss√£o insuficiente';
        break;
      case 404:
        message = 'Recurso n√£o encontrado';
        break;
      case 422:
        message = 'Dados n√£o puderam ser processados';
        break;
      case 429:
        message = 'Muitas requisi√ß√µes - tente novamente em alguns minutos';
        break;
      case 500:
        message = 'Erro interno do servidor';
        break;
      case 502:
        message = 'Servidor temporariamente indispon√≠vel';
        break;
      case 503:
        message = 'Servi√ßo em manuten√ß√£o';
        break;
      default:
        if (error.status === 0) {
          message = 'Sem conex√£o com a internet';
          code = 'NO_CONNECTION';
        }
    }

    return {
      message,
      code,
      details: error,
      timestamp: new Date()
    };
  }
}
