import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'ifrs15-revenue',
  standalone: true,
  imports: [
    CommonModule,
    MatCardModule,
    MatTableModule,
    MatButtonModule,
    MatIconModule,
    MatProgressBarModule
  ],
  template: `
    <div class="revenue-container">
      <div class="header">
        <h1>Reconhecimento de Receitas</h1>
        <button mat-raised-button color="primary">
          <mat-icon>calculate</mat-icon>
          Calcular Receitas
        </button>
      </div>

      <div class="summary-cards">
        <mat-card class="summary-card">
          <mat-card-content>
            <h3>Receita Total Reconhecida</h3>
            <div class="amount">R$ 2.450.000,00</div>
          </mat-card-content>
        </mat-card>

        <mat-card class="summary-card">
          <mat-card-content>
            <h3>Receita Pendente</h3>
            <div class="amount pending">R$ 525.000,00</div>
          </mat-card-content>
        </mat-card>
      </div>

      <mat-card>
        <mat-card-header>
          <mat-card-title>Obrigações de Performance</mat-card-title>
        </mat-card-header>
        <mat-card-content>
          <table mat-table [dataSource]="performanceObligations" class="revenue-table">
            <ng-container matColumnDef="contract">
              <th mat-header-cell *matHeaderCellDef>Contrato</th>
              <td mat-cell *matCellDef="let item">{{ item.contract }}</td>
            </ng-container>

            <ng-container matColumnDef="description">
              <th mat-header-cell *matHeaderCellDef>Descrição</th>
              <td mat-cell *matCellDef="let item">{{ item.description }}</td>
            </ng-container>

            <ng-container matColumnDef="allocatedPrice">
              <th mat-header-cell *matHeaderCellDef>Preço Alocado</th>
              <td mat-cell *matCellDef="let item">{{ item.allocatedPrice | currency:'BRL' }}</td>
            </ng-container>

            <ng-container matColumnDef="progress">
              <th mat-header-cell *matHeaderCellDef>Progresso</th>
              <td mat-cell *matCellDef="let item">
                <div class="progress-container">
                  <mat-progress-bar mode="determinate" [value]="item.progress"></mat-progress-bar>
                  <span class="progress-text">{{ item.progress }}%</span>
                </div>
              </td>
            </ng-container>

            <ng-container matColumnDef="recognizedRevenue">
              <th mat-header-cell *matHeaderCellDef>Receita Reconhecida</th>
              <td mat-cell *matCellDef="let item">{{ item.recognizedRevenue | currency:'BRL' }}</td>
            </ng-container>

            <ng-container matColumnDef="actions">
              <th mat-header-cell *matHeaderCellDef>Ações</th>
              <td mat-cell *matCellDef="let item">
                <button mat-icon-button>
                  <mat-icon>timeline</mat-icon>
                </button>
              </td>
            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
          </table>
        </mat-card-content>
      </mat-card>
    </div>
  `,
  styles: [`
    .revenue-container {
      padding: 20px;
    }

    .header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 20px;
    }

    .summary-cards {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 20px;
      margin-bottom: 30px;
    }

    .summary-card {
      text-align: center;
    }

    .amount {
      font-size: 2em;
      font-weight: bold;
      color: #4caf50;
      margin-top: 10px;
    }

    .amount.pending {
      color: #ff9800;
    }

    .revenue-table {
      width: 100%;
    }

    .progress-container {
      display: flex;
      align-items: center;
      gap: 10px;
    }

    .progress-text {
      min-width: 40px;
      font-size: 0.9em;
    }
  `]
})
export class RevenueComponent {
  displayedColumns = ['contract', 'description', 'allocatedPrice', 'progress', 'recognizedRevenue', 'actions'];

  performanceObligations = [
    {
      contract: 'CTR-001',
      description: 'Desenvolvimento de Software',
      allocatedPrice: 100000,
      progress: 75,
      recognizedRevenue: 75000
    },
    {
      contract: 'CTR-001',
      description: 'Treinamento e Suporte',
      allocatedPrice: 50000,
      progress: 40,
      recognizedRevenue: 20000
    },
    {
      contract: 'CTR-002',
      description: 'Consultoria Especializada',
      allocatedPrice: 180000,
      progress: 90,
      recognizedRevenue: 162000
    },
    {
      contract: 'CTR-002',
      description: 'Implementação',
      allocatedPrice: 100000,
      progress: 60,
      recognizedRevenue: 60000
    }
  ];
}
