import { Component, OnInit, ViewChild, TemplateRef } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { ClientsApiService } from '../../shared/services/api.service';
import { AuthService } from '../../shared/guards/rbac.guard';

export interface Client {
  id: string;
  nome: string;
  documento: string;
  email: string;
  telefone: string;
  endereco: string;
  status: 'ativo' | 'inativo' | 'suspenso';
  totalContratos: number;
  valorTotal: number;
  createdAt: Date;
  updatedAt: Date;
}

@Component({
  selector: 'app-clients-list',
  templateUrl: './clients-list.component.html',
  styleUrls: ['./clients-list.component.scss'],
  standalone: true,
  imports: [CommonModule, FormsModule]
})
export class ClientsListComponent implements OnInit {
  // Remove mat-table related properties
  // @ViewChild(MatPaginator) paginator!: MatPaginator;
  // @ViewChild(MatSort) sort!: MatSort;

  // Remove displayedColumns property since we're not using mat-table anymore
  // displayedColumns: string[] = [
  //   'nome', 
  //   'documento', 
  //   'email', 
  //   'status', 
  //   'totalContratos', 
  //   'valorTotal', 
  //   'actions'
  // ];

  // Standard array instead of MatTableDataSource
  clients: Client[] = [];
  filteredClients: Client[] = [];
  loading = false;
  searchTerm = '';
  statusFilter = '';

  // Pagination properties
  currentPage = 1;
  pageSize = 10;
  totalItems = 0;
  totalPages = 1;
  pageSizeOptions = [5, 10, 25, 50];

  // Status options
  statusOptions = [
    { value: '', label: 'Todos' },
    { value: 'ativo', label: 'Ativo' },
    { value: 'inativo', label: 'Inativo' },
    { value: 'suspenso', label: 'Suspenso' }
  ];

  constructor(
    private apiService: ClientsApiService,
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadClients();
  }

  ngAfterViewInit(): void {
    // Remove mat-table related initialization
    // this.dataSource.paginator = this.paginator;
    // this.dataSource.sort = this.sort;
  }

  loadClients(): void {
    this.loading = true;

    const filters = {
      search: this.searchTerm,
      status: this.statusFilter
    };

    this.apiService.getClients(this.currentPage, this.pageSize, filters).subscribe({
      next: (response) => {
        this.clients = response.items || [];
        this.filteredClients = [...this.clients];
        this.totalItems = response.total || 0;
        this.totalPages = Math.ceil(this.totalItems / this.pageSize);
        this.loading = false;
      },
      error: (error) => {
        console.error('Erro ao carregar clientes:', error);
        this.loading = false;
      }
    });
  }

  applyFilter(): void {
    this.currentPage = 1; // Reset to first page when filtering
    this.loadClients();
  }

  clearFilters(): void {
    this.searchTerm = '';
    this.statusFilter = '';
    this.applyFilter();
  }

  // Pagination methods
  get Math() {
    return Math;
  }

  previousPage(): void {
    if (this.currentPage > 1) {
      this.currentPage--;
      this.loadClients();
    }
  }

  nextPage(): void {
    if (this.currentPage < this.totalPages) {
      this.currentPage++;
      this.loadClients();
    }
  }

  onPageChange(): void {
    this.loadClients();
  }

  onStatusFilterChange(): void {
    this.currentPage = 1; // Reset to first page when filtering
    this.loadClients();
  }

  // Actions
  canCreate(): boolean {
    return this.authService.hasPermission('clients:create');
  }

  canEdit(): boolean {
    return this.authService.hasPermission('clients:update');
  }

  canDelete(): boolean {
    return this.authService.hasPermission('clients:delete');
  }

  canView(): boolean {
    return this.authService.hasPermission('clients:read');
  }

  createClient(): void {
    this.router.navigate(['/clients/new']);
  }

  editClient(client: Client): void {
    this.router.navigate(['/clients', client.id, 'edit']);
  }

  viewClient(client: Client): void {
    this.router.navigate(['/clients', client.id]);
  }

  deleteClient(client: Client): void {
    const dialogRef = this.dialog.open(ConfirmDialogComponent, {
      width: '400px',
      data: {
        title: 'Confirmar Exclusão',
        message: `Tem certeza que deseja excluir o cliente "${client.nome}"?`,
        confirmText: 'Excluir',
        cancelText: 'Cancelar'
      }
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        // Simulating API call - in a real app, this would call the API
        const index = this.dataSource.data.findIndex(c => c.id === client.id);
        if (index > -1) {
          const newData = [...this.dataSource.data];
          newData.splice(index, 1);
          this.dataSource.data = newData;
          this.snackBar.open('Cliente excluído com sucesso!', 'Fechar', { duration: 3000 });
        }
      }
    });
  }

  toggleClientStatus(client: Client): void {
    const newStatus = client.status === 'ativo' ? 'inativo' : 'ativo';

    // Simulating API call - in a real app, this would call the API
    client.status = newStatus;
    this.snackBar.open(`Status do cliente alterado para ${newStatus}!`, 'Fechar', { duration: 3000 });
  }

  viewContracts(client: Client): void {
    this.router.navigate(['/contracts'], { queryParams: { clientId: client.id } });
  }

  // Utility methods
  getStatusColor(status: string): string {
    switch (status) {
      case 'ativo': return 'primary';
      case 'inativo': return 'accent';
      case 'suspenso': return 'warn';
      default: return '';
    }
  }

  getStatusIcon(status: string): string {
    switch (status) {
      case 'ativo': return 'check_circle';
      case 'inativo': return 'radio_button_unchecked';
      case 'suspenso': return 'block';
      default: return 'help';
    }
  }

  exportClients(): void {
    this.loading = true;

    this.apiService.exportClients().subscribe({
      next: (blob) => {
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `clientes_${new Date().toISOString().split('T')[0]}.xlsx`;
        link.click();
        window.URL.revokeObjectURL(url);

        this.loading = false;
        this.snackBar.open('Relatório exportado com sucesso!', 'Fechar', { duration: 3000 });
      },
      error: (error) => {
        // Fallback to CSV export if API fails
        const exportData = this.dataSource.filteredData.map(client => ({
          Nome: client.nome,
          Documento: client.documento,
          Email: client.email,
          Telefone: client.telefone,
          Status: client.status,
          'Total Contratos': client.totalContratos,
          'Valor Total': client.valorTotal
        }));

        const csvContent = this.convertToCSV(exportData);
        const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `clientes_${new Date().toISOString().split('T')[0]}.csv`;
        link.click();
        window.URL.revokeObjectURL(url);

        this.loading = false;
        this.snackBar.open('Relatório exportado como CSV (fallback)', 'Fechar', { duration: 3000 });
      }
    });
  }

  private convertToCSV(data: any[]): string {
    if (!data || data.length === 0) return '';

    const headers = Object.keys(data[0]);
    const csvRows = [
      headers.join(','),
      ...data.map(row => headers.map(header => `"${row[header] || ''}"`).join(','))
    ];

    return csvRows.join('\n');
  }
}

// Confirm Dialog Component (inline for simplicity)
@Component({
  selector: 'app-confirm-dialog',
  template: `
    <h2>{{ data.title }}</h2>
    <div class="dialog-content">
      <p>{{ data.message }}</p>
    </div>
    <div class="dialog-actions">
      <button class="btn btn-secondary" (click)="onCancel()">{{ data.cancelText }}</button>
      <button class="btn btn-danger" (click)="onConfirm()">{{ data.confirmText }}</button>
    </div>
  `,
  standalone: false
})
export class ConfirmDialogComponent {
  constructor(
    public dialogRef: MatDialogRef<ConfirmDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) {}

  onConfirm(): void {
    this.dialogRef.close(true);
  }

  onCancel(): void {
    this.dialogRef.close(false);
  }
}
<div class="clients-container">
  <!-- Header -->
  <div class="header-section">
    <div class="page-title">
      <h1>
        👥 Clientes
      </h1>
      <p class="page-description">Gerencie seus clientes e contratos IFRS 15</p>
    </div>

    <div class="header-actions">
        <button class="btn btn-primary" (click)="addClient()" *ngIf="canCreate()">
          ➕ Novo Cliente
        </button>

        <button class="btn btn-secondary" (click)="exportClients()" [disabled]="loading">
          📥 Exportar
        </button>
      </div>
  </div>

  <!-- Filtros -->
  <div class="filters-card">
    <div class="card-content">
      <div class="filters-row">
        <div class="form-field search-field">
          <label>Buscar clientes</label>
          <input [(ngModel)]="searchTerm" 
                 (keyup.enter)="applyFilter()"
                 placeholder="Nome, email ou documento">
          <span class="suffix">🔍</span>
        </div>

        <div class="form-field">
          <label>Status</label>
          <select [(ngModel)]="statusFilter" (change)="applyFilter()">
            <option *ngFor="let status of statusOptions" [value]="status.value">
              {{ status.label }}
            </option>
          </select>
        </div>

        <div class="filter-actions">
          <button class="btn btn-accent" (click)="applyFilter()" [disabled]="loading">
            🔍 Filtrar
          </button>

          <button class="btn btn-secondary" (click)="clearFilters()">
            ❌ Limpar
          </button>
        </div>
      </div>
    </div>
  </div>

  <!-- Data Table -->
  <div class="table-card">
    <div class="table-content">
      <div class="table-container">
        <table class="clients-table">
          <thead>
            <tr>
              <th>Nome</th>
              <th>Email</th>
              <th>Telefone</th>
              <th>Status</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let client of dataSource.data">
              <td>
                <div class="client-info">
                  <div class="client-name">{{ client.nome }}</div>
                  <div class="client-document">{{ client.documento }}</div>
                </div>
              </td>
              <td>{{ client.email }}</td>
              <td>{{ client.telefone }}</td>
              <td>
                <span class="status-badge" [ngClass]="'status-' + client.status">
                  {{ client.status }}
                </span>
              </td>
              <td>
                <div class="action-buttons">
                  <button class="btn-icon" (click)="editClient(client)" title="Editar">
                    ✏️
                  </button>
                  <button class="btn-icon" (click)="viewContracts(client)" title="Contratos">
                    📄
                  </button>
                  <button class="btn-icon" (click)="changeStatus(client)" title="Alterar Status">
                    🔄
                  </button>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
      </div>

      <!-- Loading State -->
      <div *ngIf="loading" class="loading-container">
        <div class="loading-spinner">Carregando...</div>
      </div>

      <!-- Empty State -->
      <div *ngIf="!loading && dataSource.data.length === 0" class="empty-state">
        <p>Nenhum cliente encontrado.</p>
      </div>

      <!-- Pagination Controls -->
      <div class="pagination-container">
        <div class="pagination-info">
          Mostrando {{ (currentPage - 1) * pageSize + 1 }} - 
          {{ Math.min(currentPage * pageSize, totalItems) }} de {{ totalItems }} itens
        </div>
        <div class="pagination-controls">
          <button 
            mat-icon-button 
            [disabled]="currentPage <= 1" 
            (click)="previousPage()"
            class="pagination-button">
            ←
          </button>

          <span class="pagination-info">
            Página {{ currentPage }} de {{ totalPages }} ({{ totalItems }} itens)
          </span>

          <button 
            mat-icon-button 
            [disabled]="currentPage >= totalPages" 
            (click)="nextPage()"
            class="pagination-button">
            →
          </button>
        </div>
      </div>
    </div>
  </div>

  <!-- Summary Cards -->
  <div class="summary-section" *ngIf="!loading && dataSource.data.length > 0">
    <div class="summary-cards">
      <div class="summary-card">
        <div class="summary-content">
          <div class="metric">
            <span class="metric-icon">👥</span>
            <div class="metric-content">
              <span class="metric-value">{{ totalItems }}</span>
              <span class="metric-label">Total de Clientes</span>
            </div>
          </div>
        </div>
      </div>

      <div class="summary-card">
        <div class="summary-content">
          <div class="metric">
            <span class="metric-icon">✅</span>
            <div class="metric-content">
              <span class="metric-value">{{ getActiveClientsCount() }}</span>
              <span class="metric-label">Clientes Ativos</span>
            </div>
          </div>
        </div>
      </div>

      <div class="summary-card">
        <div class="summary-content">
          <div class="metric">
            <span class="metric-icon">📄</span>
            <div class="metric-content">
              <span class="metric-value">{{ getTotalContracts() }}</span>
              <span class="metric-label">Total de Contratos</span>
            </div>
          </div>
        </div>
      </div>

      <div class="summary-card">
        <div class="summary-content">
          <div class="metric">
            <span class="metric-icon">💰</span>
            <div class="metric-content">
              <span class="metric-value">{{ getTotalValue() | currency:'BRL':'symbol':'1.0-0' }}</span>
              <span class="metric-label">Valor Total</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
