import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'ifrs15-contracts',
  standalone: true,
  imports: [
    CommonModule,
    MatCardModule,
    MatTableModule,
    MatButtonModule,
    MatIconModule,
    MatChipsModule
  ],
  template: `
    <div class="contracts-container">
      <div class="header">
        <h1>Contratos</h1>
        <button mat-raised-button color="primary">
          <mat-icon>add</mat-icon>
          Novo Contrato
        </button>
      </div>

      <mat-card>
        <mat-card-content>
          <table mat-table [dataSource]="contracts" class="contracts-table">
            <ng-container matColumnDef="id">
              <th mat-header-cell *matHeaderCellDef>ID</th>
              <td mat-cell *matCellDef="let contract">{{ contract.id }}</td>
            </ng-container>

            <ng-container matColumnDef="customer">
              <th mat-header-cell *matHeaderCellDef>Cliente</th>
              <td mat-cell *matCellDef="let contract">{{ contract.customer }}</td>
            </ng-container>

            <ng-container matColumnDef="value">
              <th mat-header-cell *matHeaderCellDef>Valor</th>
              <td mat-cell *matCellDef="let contract">{{ contract.value | currency:'BRL' }}</td>
            </ng-container>

            <ng-container matColumnDef="status">
              <th mat-header-cell *matHeaderCellDef>Status</th>
              <td mat-cell *matCellDef="let contract">
                <mat-chip [color]="getStatusColor(contract.status)">
                  {{ contract.status }}
                </mat-chip>
              </td>
            </ng-container>

            <ng-container matColumnDef="startDate">
              <th mat-header-cell *matHeaderCellDef>Data Início</th>
              <td mat-cell *matCellDef="let contract">{{ contract.startDate | date:'dd/MM/yyyy' }}</td>
            </ng-container>

            <ng-container matColumnDef="actions">
              <th mat-header-cell *matHeaderCellDef>Ações</th>
              <td mat-cell *matCellDef="let contract">
                <button mat-icon-button>
                  <mat-icon>edit</mat-icon>
                </button>
                <button mat-icon-button>
                  <mat-icon>visibility</mat-icon>
                </button>
              </td>
            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
          </table>
        </mat-card-content>
      </mat-card>
    </div>
  `,
  styles: [`
    .contracts-container {
      padding: 20px;
    }

    .header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 20px;
    }

    .contracts-table {
      width: 100%;
    }
  `]
})
export class ContractsComponent {
  displayedColumns = ['id', 'customer', 'value', 'status', 'startDate', 'actions'];

  contracts = [
    {
      id: 'CTR-001',
      customer: 'Empresa ABC Ltda',
      value: 150000,
      status: 'ativo',
      startDate: new Date('2024-01-15')
    },
    {
      id: 'CTR-002',
      customer: 'Corporação XYZ S.A.',
      value: 280000,
      status: 'em_andamento',
      startDate: new Date('2024-02-01')
    },
    {
      id: 'CTR-003',
      customer: 'Indústria 123 Ltda',
      value: 95000,
      status: 'concluido',
      startDate: new Date('2023-12-10')
    }
  ];

  getStatusColor(status: string): string {
    switch (status) {
      case 'ativo': return 'primary';
      case 'em_andamento': return 'accent';
      case 'concluido': return 'warn';
      default: return '';
    }
  }
}
