import { Component, OnInit, Inject } from '@angular/core';
import { FormBuilder, FormGroup, FormArray, Validators, ReactiveFormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { Router, ActivatedRoute } from '@angular/router';
import { MatDialogModule } from '@angular/material/dialog';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatCardModule } from '@angular/material/card';
import { MatDatepickerModule } from '@angular/material/datepicker';
import { MatNativeDateModule } from '@angular/material/core';
import { MatTooltipModule } from '@angular/material/tooltip';
import { MatDividerModule } from '@angular/material/divider';
import { MatChipsModule } from '@angular/material/chips';
import { ContractsApiService } from '../../shared/services/api.service';

export interface PerformanceObligation {
  id?: string;
  descricao: string;
  valor: number;
  metodoReconhecimento: string;
  dataInicio: Date;
  dataFim: Date;
  progresso: number;
}

@Component({
  selector: 'app-contract-form',
  standalone: true,
  imports: [
    CommonModule, 
    ReactiveFormsModule,
    MatDialogModule,
    MatFormFieldModule,
    MatInputModule,
    MatSelectModule,
    MatButtonModule,
    MatIconModule,
    MatCardModule,
    MatDatepickerModule,
    MatNativeDateModule,
    MatTooltipModule,
    MatDividerModule,
    MatChipsModule
  ],
  templateUrl: './contract-form.component.html',
  styleUrls: ['./contract-form.component.scss']
})
export class ContractFormComponent implements OnInit {
  contractForm!: FormGroup;
  loading = false;
  isEditMode = false;

  reconhecimentoMethods = [
    { value: 'point_in_time', label: 'Ponto no Tempo' },
    { value: 'over_time_output', label: 'Ao Longo do Tempo - Output' },
    { value: 'over_time_input', label: 'Ao Longo do Tempo - Input' },
    { value: 'over_time_time_elapsed', label: 'Ao Longo do Tempo - Tempo Decorrido' }
  ];

  statusOptions = [
    { value: 'draft', label: 'Rascunho' },
    { value: 'active', label: 'Ativo' },
    { value: 'completed', label: 'Concluído' },
    { value: 'cancelled', label: 'Cancelado' }
  ];

  constructor(
    private fb: FormBuilder,
    private contractsApi: ContractsApiService,
    private router: Router,
    private route: ActivatedRoute
  ) {
    this.isEditMode = this.route.snapshot.url.some(segment => segment.path === 'edit');
  }

  ngOnInit(): void {
    this.initializeForm();

    const contractId = this.route.snapshot.paramMap.get('id');
    if (this.isEditMode && contractId) {
      this.loadContractById(contractId);
    }
  }

  initializeForm(): void {
    this.contractForm = this.fb.group({
      numero: ['', [Validators.required, Validators.minLength(3)]],
      cliente: ['', [Validators.required, Validators.minLength(2)]],
      descricao: ['', Validators.required],
      dataInicio: ['', Validators.required],
      dataFim: ['', Validators.required],
      valor: ['', [Validators.required, Validators.min(0.01)]],
      status: ['draft', Validators.required],
      moeda: ['BRL', Validators.required],
      observacoes: [''],
      performanceObligations: this.fb.array([])
    });

    // Add initial PO
    this.addPerformanceObligation();
  }

  get performanceObligations(): FormArray {
    return this.contractForm.get('performanceObligations') as FormArray;
  }

  createPerformanceObligationForm(po?: PerformanceObligation): FormGroup {
    return this.fb.group({
      id: [po?.id || ''],
      descricao: [po?.descricao || '', [Validators.required, Validators.minLength(3)]],
      valor: [po?.valor || '', [Validators.required, Validators.min(0.01)]],
      metodoReconhecimento: [po?.metodoReconhecimento || 'over_time_output', Validators.required],
      dataInicio: [po?.dataInicio || '', Validators.required],
      dataFim: [po?.dataFim || '', Validators.required],
      progresso: [po?.progresso || 0, [Validators.min(0), Validators.max(100)]]
    });
  }

  addPerformanceObligation(): void {
    this.performanceObligations.push(this.createPerformanceObligationForm());
  }

  removePerformanceObligation(index: number): void {
    if (this.performanceObligations.length > 1) {
      this.performanceObligations.removeAt(index);
    }
  }

  loadContractData(contract: any): void {
    this.contractForm.patchValue({
      numero: contract.numero,
      cliente: contract.cliente,
      descricao: contract.descricao,
      dataInicio: contract.dataInicio,
      dataFim: contract.dataFim,
      valor: contract.valor,
      status: contract.status,
      moeda: contract.moeda || 'BRL',
      observacoes: contract.observacoes || ''
    });

    // Clear existing POs and add from contract
    this.performanceObligations.clear();
    if (contract.performanceObligations?.length) {
      contract.performanceObligations.forEach((po: PerformanceObligation) => {
        this.performanceObligations.push(this.createPerformanceObligationForm(po));
      });
    } else {
      this.addPerformanceObligation();
    }
  }

  onSubmit(): void {
    if (this.contractForm.valid) {
      this.loading = true;
      const formData = this.contractForm.value;

      const apiCall = this.isEditMode 
        ? this.contractsApi.updateContract(this.data.contract.id, formData)
        : this.contractsApi.createContract(formData);

      apiCall.subscribe({
        next: (result) => {
          console.log(this.isEditMode ? 'Contrato atualizado com sucesso!' : 'Contrato criado com sucesso!');
          // Emit event or navigate back
        },
        error: (error) => {
          console.error(`Erro ao ${this.isEditMode ? 'atualizar' : 'criar'} contrato:`, error.message);
          this.loading = false;
        }
      });
    } else {
      this.markFormGroupTouched(this.contractForm);
    }
  }

  onCancel(): void {
    // Navigate back or emit cancel event
    console.log('Form cancelled');
  }

  private markFormGroupTouched(formGroup: FormGroup): void {
    Object.keys(formGroup.controls).forEach(key => {
      const control = formGroup.get(key);
      control?.markAsTouched();

      if (control instanceof FormGroup) {
        this.markFormGroupTouched(control);
      } else if (control instanceof FormArray) {
        control.controls.forEach(arrayControl => {
          if (arrayControl instanceof FormGroup) {
            this.markFormGroupTouched(arrayControl);
          }
        });
      }
    });
  }

  getErrorMessage(fieldName: string, formGroup?: FormGroup): string {
    const control = (formGroup || this.contractForm).get(fieldName);

    if (control?.hasError('required')) {
      return 'Este campo é obrigatório';
    }
    if (control?.hasError('minlength')) {
      return `Mínimo de ${control.errors?.['minlength'].requiredLength} caracteres`;
    }
    if (control?.hasError('min')) {
      return `Valor deve ser maior que ${control.errors?.['min'].min}`;
    }
    if (control?.hasError('max')) {
      return `Valor deve ser menor que ${control.errors?.['max'].max}`;
    }

    return '';
  }

  getTotalPOValue(): number {
    return this.performanceObligations.controls.reduce((total, control) => {
      const valor = control.get('valor')?.value || 0;
      return total + Number(valor);
    }, 0);
  }

  isValueMismatch(): boolean {
    const contractValue = this.contractForm.get('valor')?.value || 0;
    const totalPOValue = this.getTotalPOValue();
    return Math.abs(contractValue - totalPOValue) > 0.01;
  }

  goBack(): void {
    this.router.navigate(['/contracts']);
  }

  onCancel(): void {
    this.goBack();
  }
}
<div class="contract-form-container">
  <form [formGroup]="contractForm" (ngSubmit)="onSubmit()">

    <!-- Header -->
    <div class="form-header">
      <h2 mat-dialog-title>
        <mat-icon>{{ isEditMode ? 'edit' : 'add' }}</mat-icon>
        {{ isEditMode ? 'Editar Contrato' : 'Novo Contrato' }}
      </h2>
      <p class="form-subtitle">
        {{ isEditMode ? 'Atualize as informações do contrato' : 'Preencha os dados para criar um novo contrato IFRS 15' }}
      </p>
    </div>

    <mat-dialog-content class="form-content">

      <!-- Basic Information -->
      <mat-card class="form-section">
        <mat-card-header>
          <mat-card-title>Informações Básicas</mat-card-title>
        </mat-card-header>
        <mat-card-content>
          <div class="form-row">
            <mat-form-field appearance="outline" class="form-field">
              <mat-label>Número do Contrato</mat-label>
              <input matInput formControlName="numero" placeholder="CTR-2024-001">
              <mat-icon matSuffix matTooltip="Identificador único do contrato">info</mat-icon>
              <mat-error>{{ getErrorMessage('numero') }}</mat-error>
            </mat-form-field>

            <mat-form-field appearance="outline" class="form-field">
              <mat-label>Status</mat-label>
              <mat-select formControlName="status">
                <mat-option *ngFor="let status of statusOptions" [value]="status.value">
                  {{ status.label }}
                </mat-option>
              </mat-select>
              <mat-error>{{ getErrorMessage('status') }}</mat-error>
            </mat-form-field>
          </div>

          <div class="form-row">
            <mat-form-field appearance="outline" class="form-field-full">
              <mat-label>Cliente</mat-label>
              <input matInput formControlName="cliente" placeholder="Nome da empresa cliente">
              <mat-icon matSuffix matTooltip="Nome ou razão social do cliente">business</mat-icon>
              <mat-error>{{ getErrorMessage('cliente') }}</mat-error>
            </mat-form-field>
          </div>

          <div class="form-row">
            <mat-form-field appearance="outline" class="form-field-full">
              <mat-label>Descrição do Contrato</mat-label>
              <textarea matInput formControlName="descricao" rows="3" 
                        placeholder="Descreva o objeto do contrato e principais obrigações"></textarea>
              <mat-error>{{ getErrorMessage('descricao') }}</mat-error>
            </mat-form-field>
          </div>

          <div class="form-row">
            <mat-form-field appearance="outline" class="form-field">
              <mat-label>Data de Início</mat-label>
              <input matInput [matDatepicker]="startPicker" formControlName="dataInicio">
              <mat-datepicker-toggle matSuffix [for]="startPicker"></mat-datepicker-toggle>
              <mat-datepicker #startPicker></mat-datepicker>
              <mat-error>{{ getErrorMessage('dataInicio') }}</mat-error>
            </mat-form-field>

            <mat-form-field appearance="outline" class="form-field">
              <mat-label>Data de Fim</mat-label>
              <input matInput [matDatepicker]="endPicker" formControlName="dataFim">
              <mat-datepicker-toggle matSuffix [for]="endPicker"></mat-datepicker-toggle>
              <mat-datepicker #endPicker></mat-datepicker>
              <mat-error>{{ getErrorMessage('dataFim') }}</mat-error>
            </mat-form-field>
          </div>

          <div class="form-row">
            <mat-form-field appearance="outline" class="form-field">
              <mat-label>Valor Total</mat-label>
              <input matInput type="number" formControlName="valor" placeholder="0.00">
              <span matPrefix>R$ </span>
              <mat-icon matSuffix matTooltip="Preço de transação total do contrato">monetization_on</mat-icon>
              <mat-error>{{ getErrorMessage('valor') }}</mat-error>
            </mat-form-field>

            <mat-form-field appearance="outline" class="form-field">
              <mat-label>Moeda</mat-label>
              <mat-select formControlName="moeda">
                <mat-option value="BRL">Real (BRL)</mat-option>
                <mat-option value="USD">Dólar (USD)</mat-option>
                <mat-option value="EUR">Euro (EUR)</mat-option>
              </mat-select>
              <mat-error>{{ getErrorMessage('moeda') }}</mat-error>
            </mat-form-field>
          </div>
        </mat-card-content>
      </mat-card>

      <!-- Performance Obligations -->
      <mat-card class="form-section">
        <mat-card-header>
          <mat-card-title>
            Performance Obligations
            <mat-chip class="po-count">{{ performanceObligations.length }}</mat-chip>
          </mat-card-title>
          <mat-card-subtitle>
            Identifique as obrigações de performance distintas do contrato
          </mat-card-subtitle>
        </mat-card-header>
        <mat-card-content>

          <!-- Value Validation Alert -->
          <mat-card *ngIf="isValueMismatch()" class="validation-alert warn">
            <mat-card-content>
              <div class="alert-content">
                <mat-icon>warning</mat-icon>
                <div>
                  <strong>Divergência de Valores</strong>
                  <p>
                    Valor do contrato: {{ contractForm.get('valor')?.value | currency:'BRL':'symbol':'1.2-2' }} | 
                    Total das POs: {{ getTotalPOValue() | currency:'BRL':'symbol':'1.2-2' }}
                  </p>
                </div>
              </div>
            </mat-card-content>
          </mat-card>

          <div formArrayName="performanceObligations">
            <div *ngFor="let po of performanceObligations.controls; let i = index" 
                 [formGroupName]="i" class="po-item">

              <div class="po-header">
                <h4>Performance Obligation {{ i + 1 }}</h4>
                <button type="button" mat-icon-button color="warn" 
                        (click)="removePerformanceObligation(i)"
                        [disabled]="performanceObligations.length <= 1"
                        matTooltip="Remover PO">
                  <mat-icon>delete</mat-icon>
                </button>
              </div>

              <div class="po-content">
                <div class="form-row">
                  <mat-form-field appearance="outline" class="form-field-full">
                    <mat-label>Descrição da Obrigação</mat-label>
                    <input matInput formControlName="descricao" 
                           placeholder="Ex: Licença de software, Implementação, Suporte">
                    <mat-error>{{ getErrorMessage('descricao', po) }}</mat-error>
                  </mat-form-field>
                </div>

                <div class="form-row">
                  <mat-form-field appearance="outline" class="form-field">
                    <mat-label>Valor Alocado</mat-label>
                    <input matInput type="number" formControlName="valor" placeholder="0.00">
                    <span matPrefix>R$ </span>
                    <mat-error>{{ getErrorMessage('valor', po) }}</mat-error>
                  </mat-form-field>

                  <mat-form-field appearance="outline" class="form-field">
                    <mat-label>Método de Reconhecimento</mat-label>
                    <mat-select formControlName="metodoReconhecimento">
                      <mat-option *ngFor="let method of reconhecimentoMethods" [value]="method.value">
                        {{ method.label }}
                      </mat-option>
                    </mat-select>
                    <mat-icon matSuffix matTooltip="Conforme IFRS 15 - critérios de transferência de controle">help</mat-icon>
                    <mat-error>{{ getErrorMessage('metodoReconhecimento', po) }}</mat-error>
                  </mat-form-field>
                </div>

                <div class="form-row">
                  <mat-form-field appearance="outline" class="form-field">
                    <mat-label>Data de Início</mat-label>
                    <input matInput [matDatepicker]="poStartPicker" formControlName="dataInicio">
                    <mat-datepicker-toggle matSuffix [for]="poStartPicker"></mat-datepicker-toggle>
                    <mat-datepicker #poStartPicker></mat-datepicker>
                    <mat-error>{{ getErrorMessage('dataInicio', po) }}</mat-error>
                  </mat-form-field>

                  <mat-form-field appearance="outline" class="form-field">
                    <mat-label>Data de Fim</mat-label>
                    <input matInput [matDatepicker]="poEndPicker" formControlName="dataFim">
                    <mat-datepicker-toggle matSuffix [for]="poEndPicker"></mat-datepicker-toggle>
                    <mat-datepicker #poEndPicker></mat-datepicker>
                    <mat-error>{{ getErrorMessage('dataFim', po) }}</mat-error>
                  </mat-form-field>
                </div>

                <div class="form-row" *ngIf="isEditMode">
                  <mat-form-field appearance="outline" class="form-field">
                    <mat-label>Progresso (%)</mat-label>
                    <input matInput type="number" formControlName="progresso" min="0" max="100">
                    <span matSuffix>%</span>
                    <mat-error>{{ getErrorMessage('progresso', po) }}</mat-error>
                  </mat-form-field>

                  <div class="progress-visual">
                    <mat-progress-bar mode="determinate" 
                                      [value]="po.get('progresso')?.value || 0">
                    </mat-progress-bar>
                    <span class="progress-label">
                      {{ po.get('progresso')?.value || 0 }}% concluído
                    </span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div class="po-actions">
            <button type="button" mat-stroked-button (click)="addPerformanceObligation()">
              <mat-icon>add</mat-icon>
              Adicionar Performance Obligation
            </button>
          </div>
        </mat-card-content>
      </mat-card>

      <!-- Additional Information -->
      <mat-card class="form-section">
        <mat-card-header>
          <mat-card-title>Informações Adicionais</mat-card-title>
        </mat-card-header>
        <mat-card-content>
          <mat-form-field appearance="outline" class="form-field-full">
            <mat-label>Observações</mat-label>
            <textarea matInput formControlName="observacoes" rows="4" 
                      placeholder="Informações adicionais, considerações especiais, etc."></textarea>
          </mat-form-field>
        </mat-card-content>
      </mat-card>

    </mat-dialog-content>

    <!-- Actions -->
    <mat-dialog-actions class="form-actions">
      <button type="button" mat-button (click)="onCancel()" [disabled]="loading">
        Cancelar
      </button>

      <button type="submit" mat-raised-button color="primary" [disabled]="loading || contractForm.invalid">
        <mat-icon *ngIf="loading">hourglass_empty</mat-icon>
        <mat-icon *ngIf="!loading">{{ isEditMode ? 'save' : 'add' }}</mat-icon>
        {{ loading ? 'Salvando...' : (isEditMode ? 'Atualizar' : 'Criar Contrato') }}
      </button>
    </mat-dialog-actions>

  </form>
</div>
