import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'ifrs15-contracts',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="contracts-container">
      <div class="header">
        <h1>Contratos</h1>
        <button class="btn btn-primary">
          <span>‚ûï</span>
          Novo Contrato
        </button>
      </div>

      <div class="contracts-card">
        <div class="card-content">
          <table class="contracts-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Cliente</th>
                <th>Valor</th>
                <th>Status</th>
                <th>Data In√≠cio</th>
                <th>A√ß√µes</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let contract of contracts">
                <td>{{ contract.id }}</td>
                <td>{{ contract.customer }}</td>
                <td>{{ contract.value | currency:'BRL' }}</td>
                <td>
                  <span class="status-chip" [ngClass]="'status-' + contract.status">
                    {{ getStatusLabel(contract.status) }}
                  </span>
                </td>
                <td>{{ contract.startDate | date:'dd/MM/yyyy' }}</td>
                <td>
                  <button class="btn-icon" title="Editar">
                    <span>‚úèÔ∏è</span>
                  </button>
                  <button class="btn-icon" title="Visualizar">
                    <span>üëÅÔ∏è</span>
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .contracts-container {
      padding: 20px;
    }

    .header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 20px;
    }

    .contracts-table {
      width: 100%;
    }
  `]
})
export class ContractsComponent {
  displayedColumns = ['id', 'customer', 'value', 'status', 'startDate', 'actions'];

  contracts = [
    {
      id: 'CTR-001',
      customer: 'Empresa ABC Ltda',
      value: 150000,
      status: 'ativo',
      startDate: new Date('2024-01-15')
    },
    {
      id: 'CTR-002',
      customer: 'Corpora√ß√£o XYZ S.A.',
      value: 280000,
      status: 'em_andamento',
      startDate: new Date('2024-02-01')
    },
    {
      id: 'CTR-003',
      customer: 'Ind√∫stria 123 Ltda',
      value: 95000,
      status: 'concluido',
      startDate: new Date('2023-12-10')
    }
  ];

  getStatusColor(status: string): string {
    switch (status) {
      case 'ativo': return 'primary';
      case 'em_andamento': return 'accent';
      case 'concluido': return 'warn';
      default: return '';
    }
  }
}
